export default function ForgotPasswordScreen({ email }) {
  return (
    <div className="h-screen w-screen flex items-center justify-center">
      <div className="flex items-end h-16 absolute top-2 left-4">
        <LoginScreenIcon />
        <h1
          className="ml-4 font-spectral uppercase text-2xl font-bold text-indigo-500"
          style={{ marginBottom: "-0.59rem" }}
        >
          APPEARZ
        </h1>
      </div>
      <div>
        <ForgotPasswordIcon />
        <p className=" font-lexend font-normal text-2xl text-center">
          Weve sent a password reset request to your registered email ID <br />
          <br />
          <span className="font-bold">{email}.</span>
          <br />
          <br />
          Follow the steps in the mail to reset your password.
        </p>
      </div>
    </div>
  );
}

export const getServerSideProps = (context) => {
  let { email } = context.query;
  return {
    props: {
      email,
    },
  };
};
